def BOL(canvas, word, d, delta, turtle, color=solarized['base03']):
    draw = ImageDraw.Draw(canvas.img)

    for s in word:
        match s:
            case 'F':
                x, y = turtle()
                turtle.forward(d)
                draw.line((x, y) + turtle(), fill=color)
            case 'R':
                x, y = turtle()
                turtle.forward(d)
                draw.line((x, y) + turtle(), fill=color)
                turtle.right(delta)
                x, y = turtle()
                turtle.forward(d)
                draw.line((x, y) + turtle(), fill=color)
            case 'L':
                x, y = turtle()
                turtle.forward(d)
                draw.line((x, y) + turtle(), fill=color)
                turtle.left(delta)
                x, y = turtle()
                turtle.forward(d)
                draw.line((x, y) + turtle(), fill=color)
            case 'f':
                turtle.forward(d)
            case '+':
                turtle.right(delta)
            case '-':
                turtle.left(delta)
            case '[':
                turtle.push()
            case ']':
                turtle.pop()


