TreeC = {'P': {'F': 'FF-[-F+F+F]+[+F-F-F]'}, 'axiom': 'F'}
TreeD = {'P': {'F': 'FF-[-F+F+F]+[+F-F-F]'}, 'axiom': 'F'}


def apply(n: int, system):
    '''
    Given a system (a dict with production rules and an axiom, evolve it n steps
    '''
    P = system['P']
    axiom = system['axiom']
    state = axiom
    for _ in range(n):
        next_state = ''
        for c in state:
            if c in P:
                next_state += P[c]
            else:
                next_state += c
        state = next_state
    return state
